@register.simple_tag
def project_card_image(project):
    """
    Return the URL for a project's card image.
    Uses the project.get_card_image_url() method.
    
    Args:
        project: Project instance
        
    Returns:
        str: URL to the card image
    """
    return project.get_card_image_url()
